const addEventCallback = require("addEventCallback");
const copyFromWindow = require("copyFromWindow");
const setInWindow = require("setInWindow");
const sendPixel = require("sendPixel");
const encodeUriComponent = require("encodeUriComponent");
const getUrl = require("getUrl");
const makeString = require("makeString");
/**
 * This is required even though it wouldn't be used here
 * because the tests fail without it.
 */
const log = require("logToConsole");

const VERSION = '1.1';

const VALIDATION_ERRORS = "elevar_gtm_errors";
const TAG_INFO = "elevar_gtm_tag_info";
const DATA_LAYER = "dataLayer";
const PAGE_URL = getUrl();

const getEventName = (eventId, dataLayer) => {
  const items = dataLayer.filter(item => 
    item["gtm.uniqueEventId"] === eventId
  );
  return items.length > 0 ? items[0].event : false;
};

/**
 * This function gets the tag information if it exists for the event & variable combo
 *
 * @param tags: Array of tag information that have fired since the last monitoring pixel was sent
 * @param eventId: The id of the event the current error occurred on
 * @param variableName: The GTM name of the variable that errored
 */
const getTagWithVariable = (tags, eventId, variableName) => {
  if (!tags) return [];
  return tags
    .filter((tag) => tag.eventId === eventId)
    .filter(
      (tag) =>
        tag.variables &&
        tag.variables.length &&
        tag.variables.indexOf(variableName) !== -1
    );
};

const makeUrl = (
  ctid,
  idx,
  event_name,
  variable_name,
  channels,
  tag_names,
  dlKey,
  dlValue,
  cond,
  condValue,
  url,
  validationVersion
) => {
  const enc = (value) => encodeUriComponent(makeString(value));

  return (
    "https://monitoring.getelevar.com/track.gif?vc=" +
    enc(VERSION) +
    "&vv=" +
    enc(validationVersion) +
    "&ctid=" +
    enc(ctid) +
    "&idx=" +
    enc(idx) +
    "&event_name=" +
    enc(event_name) +
    "&variable_name=" +
    enc(variable_name) +
    "&channels=" +
    enc(channels) +
    "&tag_names=" +
    enc(tag_names) +
    "&dlKey=" +
    enc(dlKey) +
    "&dlValue=" +
    enc(dlValue) +
    "&cond=" +
    enc(cond) +
    "&condValue=" +
    enc(condValue) +
    "&url=" +
    enc(url)
  );
};

const sendPixelPerTag = (containerId, index, eventName, errorEvent, page_url, tags, validationVersion) => {
  const sendPixelForTag = (tag) => {
    let url = makeUrl(
      containerId,
      index,
      eventName,
      errorEvent.variableName,
      tag && tag.channel ? tag.channel : "",
      tag && tag.tagName ? tag.tagName : "",
      errorEvent.dataLayerKey,
      errorEvent.error.value,
      errorEvent.error.condition,
      errorEvent.error.conditionValue,
      page_url,
      validationVersion
    );

    log("pixel url = ", url);
    if (!data.debugMode) {
      sendPixel(url);
    }
  };

  if (tags.length > 0) {
    tags.forEach(sendPixelForTag);
  } else {
    sendPixelForTag(undefined);
  }
};

// Fires after all tags for the trigger have completed
addEventCallback(function (containerId, _eventData) {
  const dataLayer = copyFromWindow(DATA_LAYER);
  const errors = copyFromWindow(VALIDATION_ERRORS);
  const tagInfo = copyFromWindow(TAG_INFO);

  // This removes the data from window after they have been read
  setInWindow(VALIDATION_ERRORS, [], true);
  setInWindow(TAG_INFO, [], true);

  // Send Pixel if there are errors
  if (errors && errors.length > 0) {
    errors.forEach((errorEvent, index) => {
      const eventName = errorEvent.eventName ?
            errorEvent.eventName :
            getEventName(errorEvent.eventId, dataLayer);

      const tagsUsed = getTagWithVariable(
        tagInfo,
        errorEvent.eventId,
        errorEvent.variableName
      );

      sendPixelPerTag(containerId, index, eventName ? eventName : '', errorEvent, PAGE_URL, tagsUsed, errorEvent.version || "1.0");
    });
  }
});

data.gtmOnSuccess();


