const copyFromDataLayer = require("copyFromDataLayer");
const encodeUriComponent = require("encodeUriComponent");
const getContainerVersion = require("getContainerVersion");
const getUrl = require("getUrl");
const JSON = require("JSON");
const logToConsole = require("logToConsole");
const makeInteger = require("makeInteger");
const makeString = require("makeString");
const sendPixel = require("sendPixel");

/****************** UTILITIES ******************/

const log = msg => {
  if (data.debug) {
    logToConsole(msg);
  }
};

const getObjectKeys = object => {
  let keys = [];
  for (const name in object) {
    keys.push(name);
  }
  return keys;
};

/*************** END UTILITIES ****************/

const getProductIdentifier = () => {
  const productIdentifierMap = {
    product_sku: "id",
    product_id: "product_id",
    variant_id: "variant_id"
  };
  return productIdentifierMap[data.productAttributeForIdentifying];
};

const getTranslateProductFunction = () => {
  return product => ({
    id: product[getProductIdentifier()],
    item_price: product.price,
    quantity: product.quantity || "1"
  });
};

/************* HELPER FUNCTIONS ***************/

const buildLineItems = (event, ecommerce) => {
  let products = [];
  if (event === "dl_view_item") {
    products = ecommerce.detail.products;
  } else if (event === "dl_add_to_cart") {
    products = ecommerce.add.products;
  } else if (event === "dl_view_search_results") {
    products = ecommerce.impressions;
  } else if (event === "dl_purchase" || event === "dl_subscription_purchase") {
    products = ecommerce.purchase.products;
  } else if (event === "dl_begin_checkout" || event === "dl_add_payment_info") {
    products = ecommerce.checkout.products;
  }

  return products.map(getTranslateProductFunction());
};

const serialize = obj => {
  let str = [];
  for (const objKey in obj) {
    if (obj[objKey] || obj[objKey] === 0) {
      str.push(
        encodeUriComponent(makeString(objKey)) +
          "=" +
          encodeUriComponent(makeString(obj[objKey]))
      );
    }
  }
  return str.join("&");
};

const getAdditionalParams = (event, ecommerce) => {
  if (event === "dl_sign_up") {
    return { status: "registered" };
  }

  if (event === "dl_purchase" || event === "dl_subscription_purchase") {
    return {
      order_id: ecommerce.purchase.actionField.id,
      value: ecommerce.purchase.actionField.revenue,
      num_items: ecommerce.purchase.products.reduce(
        (acc, product) => acc + makeInteger(product.quantity || 1),
        0
      )
    };
  }

  return {};
};

const isEventEnabled = facebookEvent => {
  switch (facebookEvent) {
    case "AddPaymentInfo":
      return data.isAddPaymentInfoEnabled;
    case "AddToCart":
      return data.isAddToCartEnabled;
    case "CompleteRegistration":
      return data.isCompleteRegistrationEnabled;
    case "InitiateCheckout":
      return data.isInitiateCheckoutEnabled;
    case "PageView":
      return data.isPageViewEnabled;
    case "Purchase":
      return data.isPurchaseEnabled;
    case "Search":
      return data.isSearchEnabled;
    case "ViewContent":
      return data.isViewContentEnabled;
    case "SubscriptionPurchase":
      return data.isSubscriptionPurchaseEnabled;
    default:
      return false;
  }
};

const getCustomerData = () => {
  const userProperties = copyFromDataLayer("user_properties", 2);
  const marketingParams = copyFromDataLayer("marketing", 2);

  if (!userProperties && !marketingParams) {
    return "";
  }

  const userParams = {};
  if (userProperties.customer_email)
    userParams.email = userProperties.customer_email;
  if (userProperties.customer_city)
    userParams.city = userProperties.customer_city;
  if (userProperties.customer_country)
    userParams.country = userProperties.customer_country;
  if (userProperties.customer_first_name)
    userParams.firstname = userProperties.customer_first_name;
  if (userProperties.customer_last_name)
    userParams.lastname = userProperties.customer_last_name;
  if (userProperties.customer_phone)
    userParams.phone = userProperties.customer_phone;
  if (userProperties.customer_province)
    userParams.state = userProperties.customer_province;
  // Rewrite state to province code if it exists
  if (userProperties.customer_province_code)
    userParams.state = userProperties.customer_province_code;
  if (userProperties.customer_zip)
    userParams.zipcode = userProperties.customer_zip;
  if (userProperties.customer_id)
    userParams.customer_id = userProperties.customer_id;
  if (userProperties.customer_order_count)
    userParams.customer_order_count = userProperties.customer_order_count;
  if (userProperties.customer_total_spent)
    userParams.customer_total_spent = userProperties.customer_total_spent;
  if (marketingParams) {
    if (marketingParams.utm_source)
      userParams.utm_source = marketingParams.utm_source;
    if (marketingParams.utm_medium)
      userParams.utm_medium = marketingParams.utm_medium;
    if (marketingParams.utm_campaign)
      userParams.utm_campaign = marketingParams.utm_campaign;
    if (marketingParams.utm_term)
      userParams.utm_term = marketingParams.utm_term;
    if (marketingParams.utm_content)
      userParams.utm_content = marketingParams.utm_content;
    if (marketingParams.fbclid) userParams.fbclid = marketingParams.fbclid;
  }

  return serialize(userParams);
};

const getCurrencyCode = dataLayerEcommerce => {
  if (dataLayerEcommerce && dataLayerEcommerce.currencyCode) {
    return dataLayerEcommerce.currencyCode;
  }
  return data.currencyCode;
};

/*********** END HELPER FUNCTIONS *************/

const dataLayerEvent = copyFromDataLayer("event", 2);
const dataLayerEcommerce = copyFromDataLayer("ecommerce", 2);
const eventId = copyFromDataLayer("event_id", 2) || data.eventId;

log("The event that fired is: " + dataLayerEvent);
log("The ecommerce object is: " + JSON.stringify(dataLayerEcommerce));

const ga4EventsToFacebookEvents = {
  dl_user_data: "PageView",
  dl_sign_up: "CompleteRegistration",
  dl_add_to_cart: "AddToCart",
  dl_view_item: "ViewContent",
  dl_view_search_results: "Search",
  dl_begin_checkout: "InitiateCheckout",
  dl_add_payment_info: "AddPaymentInfo",
  dl_purchase: "Purchase",
  dl_subscription_purchase: "SubscriptionPurchase"
};

const isValidGa4Event =
  getObjectKeys(ga4EventsToFacebookEvents).indexOf(dataLayerEvent) !== -1;

if (
  isValidGa4Event &&
  isEventEnabled(ga4EventsToFacebookEvents[dataLayerEvent])
) {
  const lineItems = buildLineItems(dataLayerEvent, dataLayerEcommerce);

  const additionalParams = serialize(
    getAdditionalParams(dataLayerEvent, dataLayerEcommerce)
  );
  const currencyCode = getCurrencyCode(dataLayerEcommerce);
  const customerData = getCustomerData();

  log("Line items are: " + JSON.stringify(lineItems));
  log("Additional params are: " + additionalParams);
  log("Currency code is: " + currencyCode);
  log("Customer data is: " + customerData);

  const containerVersion = getContainerVersion();
  const pixelUrl =
    data.serverSideEndpoint +
    "/efbc.gif?" +
    "ctid=" +
    containerVersion["containerId"] +
    "&source=" +
    encodeUriComponent(getUrl()) +
    (data.facebookTestCode
      ? "&test_code=" + encodeUriComponent(makeString(data.facebookTestCode))
      : "") +
    "&event_name=" +
    encodeUriComponent(ga4EventsToFacebookEvents[dataLayerEvent]) +
    "&" +
    additionalParams +
    (eventId ? "&event_id=" + encodeUriComponent(makeString(eventId)) : "") +
    "&consent=" +
    data.userConsent +
    (currencyCode ? "&currency=" + currencyCode : "") +
    (lineItems.length > 0
      ? "&contents=" + encodeUriComponent(JSON.stringify(lineItems))
      : "") +
    (lineItems.length > 0 ? "&content_type=" + data.contentType + "&" : "&") +
    customerData +
    (data.isSubscriptionPurchaseEnabled && data.isSubscribeEnabled
      ? "&subscribe=1"
      : "");
  log("Pixel url is: " + pixelUrl);
  sendPixel(
    pixelUrl,
    function () {
      log("Pixel sent successfully");
      data.gtmOnSuccess();
    },
    function () {
      log("Failed to send pixel");
      data.gtmOnFailure();
    }
  );
} else {
  log(
    "Invalid event used in Facebook Server Side Events template: " +
      dataLayerEvent
  );
  data.gtmOnFailure();
}


